import React from 'react'
import '../styles/stylesc.css';
import axios from 'axios';

const ListaUsuarios = () => {
    const getProductos = async () => {
    const options = {
        method: 'GET',
        url: 'http://localhost:5000/users'
      };
      
        await axios.request(options).then((response) => {
         this.setState({datos: response.data})
        }).catch(function (error){
        console.error(error);
      });
    };

    return (
        <div className="container"> 
        <div className="item item-1">
        <h3 className="tablaH3">Usuarios</h3>
        </div>
            <div className="item item-2">
            <form>
            <table className="tablaAzul">
            <thead>
            <tr> 
                <th className="tablath">ID USUARIO</th> 
                <th className="tablath">NOMBRE</th> 
                <th className="tablath">ESTADO</th>
                <th className="tablath">ROL</th> 
            </tr>
            </thead>
            <tbody>
                { this.datos.map((elemento)=>(
                    <tr> 
                <td>{elemento._id}</td> 
                <td>{elemento.nombre}</td>
                <td>{elemento.estado}</td> 
                <td>{elemento.rol}</td>
            </tr>
            ))}
            </tbody>
            </table>
            </form>
            </div> 
        </div>
    )
}

export default ListaUsuarios
